figure.artwork:
enum {
    dns_name(0), srv_name(1), (255)
} NameType;

opaque dNSName<1..2^16-1>;
opaque SRVName<1..2^16-1>;

struct {
    NameType name_type;
    select (name_type) {
        case dns_name: dNSName;
        case srv_name: SRVName;
    } name;
} DaneClientName;

